mapper.select:
		SELECT COUNT(*)
 		  FROM MASTERINFO
          
          	  	
	          	  
	          	  AND FranchiseeNum = #{franchiseNum}
	          	  	
          	  
          	  
		      AND (Phone LIKE  '%' + #{searchKeyword} + '%'  OR Name LIKE '%' + #{searchKeyword} + '%')
		      
		      
		      	  AND LastVisit BETWEEN CONVERT(CHAR(8), DATEADD(MM,-3, GETDATE()), 112) and CONVERT(CHAR(8), GETDATE(), 112)
		      	  
		      	  AND EntFranchiseeCode = #{franchiseNum}
		      	  
		      	  
			      	  AND EntFranchiseeCode IN
			      	  
	                   #{item}
	                  
		      	  
		      
		      
		      	  AND LastVisit BETWEEN CONVERT(CHAR(8), DATEADD(MM,-6, GETDATE()), 112) and CONVERT(CHAR(8), GETDATE(), 112)
		      	  
		      	  AND EntFranchiseeCode = #{franchiseNum}
		      	  
		      	  
			      	  AND EntFranchiseeCode IN
			      	  
	                   #{item}
	                  
		      	  
		      
		      
		      	  AND LastVisit BETWEEN CONVERT(CHAR(8), DATEADD(MM,-12, GETDATE()), 112) and CONVERT(CHAR(8), GETDATE(), 112)
		      	  
		      	  AND EntFranchiseeCode = #{franchiseNum}
		      	  
		      	  
			      	  AND EntFranchiseeCode IN
			      	  
	                   #{item}

SELECT MCode, Name, Num, Status, ID, Password, LastVisit, Point, DisNum, QRCode, Phone, Birth, Adult, AdultNum, ChildNum, JoinDate, Address, EntFlag, EntTime, ExtTime, EntNumber, Memo, Picture1, Picture2, Picture3, Picture4, Picture5, Withdraw, WithdrawDate, WithdrawMemo, FranchiseeNum, FranchiseeCode, EntFranchiseeCode, Coupon1, Coupon2, Coupon3, SpecialMan, AgreeFlag, TermsFlag, AgreeDate, Photo, CASE WHEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.FranchiseeNum) != '' THEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.FranchiseeNum) WHEN A.FranchiseeNum = '8888888' THEN '모바일' ELSE '슈퍼윙즈키즈카페' END AS fName, CASE WHEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.EntFranchiseeCode) != '' THEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.EntFranchiseeCode) ELSE '슈퍼윙즈키즈카페' END AS lastVisitFname FROM ( SELECT ROW_NUMBER() OVER(ORDER BY JOINDATE DESC) AS ROW_NUM, * FROM MASTERINFO
AND FranchiseeNum = #{franchiseNum}
AND (Phone LIKE '%' + #{searchKeyword} + '%' OR Name LIKE '%' + #{searchKeyword} + '%')
AND LastVisit BETWEEN CONVERT(CHAR(8), DATEADD(MM,-3, GETDATE()), 112) and CONVERT(CHAR(8), GETDATE(), 112)
AND EntFranchiseeCode = #{franchiseNum}
AND EntFranchiseeCode IN
#{item}
AND LastVisit BETWEEN CONVERT(CHAR(8), DATEADD(MM,-6, GETDATE()), 112) and CONVERT(CHAR(8), GETDATE(), 112)
AND EntFranchiseeCode = #{franchiseNum}
AND EntFranchiseeCode IN
#{item}
AND LastVisit BETWEEN CONVERT(CHAR(8), DATEADD(MM,-12, GETDATE()), 112) and CONVERT(CHAR(8), GETDATE(), 112)
AND EntFranchiseeCode = #{franchiseNum}
AND EntFranchiseeCode IN
#{item}
) AS A
AND ROW_NUM BETWEEN ((#{currentPage} - 1) * #{pageCount} + 1) AND (#{currentPage} * #{pageCount})

SELECT Name, MCode, JoinDate, Phone, Num, CASE WHEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.FranchiseeNum) != '' THEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.FranchiseeNum) WHEN A.FranchiseeNum = '8888888' THEN '모바일' ELSE '슈퍼윙즈키즈카페' END AS fName, LastVisit, CASE WHEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.EntFranchiseeCode) != '' THEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.EntFranchiseeCode) ELSE '슈퍼윙즈키즈카페' END AS lastVisitFname FROM ( SELECT ROW_NUMBER() OVER(ORDER BY JOINDATE DESC) AS ROW_NUM, * FROM MASTERINFO
AND FranchiseeNum = #{franchiseNum}
AND (Phone LIKE '%' + #{searchKeyword} + '%' OR Name LIKE '%' + #{searchKeyword} + '%')
) AS A
AND ROW_NUM BETWEEN ((#{currentPage} - 1) * #{pageCount} + 1) AND (#{currentPage} * #{pageCount})

SELECT Name, MCode, JoinDate, Phone, Num, CASE WHEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.FranchiseeNum) != '' THEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.FranchiseeNum) WHEN A.FranchiseeNum = '8888888' THEN '모바일' ELSE '슈퍼윙즈키즈카페' END AS fName, LastVisit, CASE WHEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.EntFranchiseeCode) != '' THEN (SELECT LocalName FROM FranchiseeInfo WHERE FranchiseeNum = A.EntFranchiseeCode) ELSE '슈퍼윙즈키즈카페' END AS lastVisitFname, CASE WHEN EntFlag = '1' THEN 'O' ELSE 'X' END AS EntFlag, CASE WHEN AgreeFlag = '1' THEN 'O' ELSE 'X' END AS AgreeFlag FROM ( SELECT ROW_NUMBER() OVER(ORDER BY JOINDATE DESC) AS ROW_NUM, * FROM MASTERINFO
AND FranchiseeNum = #{franchiseNum}
AND (Phone LIKE '%' + #{searchKeyword} + '%' OR Name LIKE '%' + #{searchKeyword} + '%')
) AS A
AND ROW_NUM BETWEEN ((#{currentPage} - 1) * #{pageCount} + 1) AND (#{currentPage} * #{pageCount})

mapper.update:
UPDATE MASTERINFO
   SET  PHONE = #{phone}
       ,AGREEFLAG = #{agreeFlag}
       ,TERMSFLAG = #{termsFlag}
       
       ,AGREEDATE = GETDATE()
       
       
       ,AGREEDATE = NULL
       
       ,NAME = #{name}
 WHERE MCODE = #{mCode}

